import { Link } from '@/i18n/navigation'
import Image from 'next/image'
import { IContactPageACF } from '@/interface/contact.interface'
import { useTranslations } from 'next-intl'

export default function InfoContact({
  data,
}: {
  data?: IContactPageACF['acf']['contact_information']
}) {
  const translateContactInfo = useTranslations('ContactForm')
  return (
    <div className='h-full w-full pt-[0.625rem]'>
      {/* social */}
      {data?.social_network && (
        <div className='flex flex-col gap-[1.25rem]'>
          <div className='flex items-center gap-[0.5rem]'>
            <ICLinkSocial className='size-[1.25rem]' />
            <h3 className='xsm:text-[1rem] xsm:leading-[1.1rem] xsm:tracking-normal font-phu-du text-[1.25rem] leading-[1.5rem] font-bold tracking-[0.025rem] text-[#3B3943]'>
              {data.social_network.title}
            </h3>
          </div>
          <div className='flex items-center gap-[1.25rem]'>
            {data.social_network.social?.map((social, i) => (
              <Link
                key={i}
                href={social?.link?.url || '#'}
                target={social?.link?.target || '_self'}
              >
                <Image
                  src={social?.icon?.url || ''}
                  alt={social?.icon?.alt || ''}
                  width={social?.icon?.width || 60}
                  height={social?.icon?.height || 60}
                  className='xsm:size-[2.75rem] size-[3.75rem] object-cover'
                />
              </Link>
            ))}
          </div>
        </div>
      )}

      {/* contact */}
      <div className='xsm:mt-[1.75rem] xsm:mb-[1.875rem] mt-[2rem] mb-[1.875rem] space-y-[1.5rem]'>
        {/* Email */}
        {data?.email && (
          <Link href={`mailto:${data.email.email}`} className='flex flex-col gap-[0.625rem]'>
            <div className='flex items-center gap-[0.5rem]'>
              <ICEmail className='size-[1.25rem]' />
              <span className='xsm:text-[1rem] xsm:leading-[1.1rem] xsm:tracking-normal font-phu-du text-[1.25rem] leading-[1.5rem] font-bold tracking-[0.025rem] text-[#3B3943]'>
                {data.email.title}
              </span>
            </div>
            <p className='xsm:text-[0.875rem] xsm:leading-[1.3125rem] font-montserrat text-[1rem] leading-[1.5rem] text-[rgba(48,48,48,0.80)]'>
              {data.email.email}
            </p>
          </Link>
        )}

        {/* Phone */}
        {data?.telephone_number && (
          <Link href={`tel:${data.telephone_number.phone}`} className='flex flex-col gap-[0.625rem]'>
            <div className='flex items-center gap-[0.5rem]'>
              <ICPhone className='size-[1.25rem]' />
              <span className='xsm:text-[1rem] xsm:leading-[1.1rem] xsm:tracking-normal font-phu-du text-[1.25rem] leading-[1.5rem] font-bold tracking-[0.025rem] text-[#3B3943]'>
                {data.telephone_number.title}
              </span>
            </div>
            <p className='xsm:text-[0.875rem] xsm:leading-[1.3125rem] font-montserrat text-[1rem] leading-[1.5rem] text-[rgba(48,48,48,0.80)]'>
              Hotline: {data.telephone_number.phone}
            </p>
          </Link>
        )}

        {/* Address */}
        {data?.office_address && (
          <div className='flex flex-col gap-[0.625rem]'>
            <div className='flex items-center gap-[0.5rem]'>
              <ICLoaction className='size-[1.25rem]' />
              <span className='xsm:text-[1rem] xsm:leading-[1.1rem] xsm:tracking-normal font-phu-du text-[1.25rem] leading-[1.5rem] font-bold tracking-[0.025rem] text-[#3B3943]'>
                {data.office_address.title}
              </span>
            </div>
            <p className='xsm:text-[0.875rem] xsm:leading-[1.3125rem] font-montserrat text-[1rem] leading-[1.5rem] text-[rgba(48,48,48,0.80)]'>
              {data.office_address.address}
            </p>
          </div>
        )}
      </div>

      {/* google map */}
      {data?.google_map && (
        <div className='xsm:h-[11.6875rem] relative h-[22.8125rem] w-full shadow-[0_6px_12px_0_rgba(0,0,0,0.12)]'>
          <Image
            src={data.google_map.preview_image?.url || ''}
            alt={data.google_map.preview_image?.alt || 'Google Map'}
            fill
            className='object-cover'
          />
          <Link
            href={data.google_map.link?.url || '#'}
            target={data.google_map.link?.target || '_blank'}
            className='xsm:h-[2.9375rem] xsm:px-[0.75rem] absolute bottom-0 left-0 flex h-[3.9375rem] w-full items-center bg-[linear-gradient(230deg,#03328C_5.76%,#00804D_100.15%)] px-[1.5rem] py-[1rem]'
          >
            <div className='flex w-full items-center justify-between'>
              <span className='xsm:text-[1rem] xsm:font-medium xsm:leading-[1rem] xsm:tracking-normal font-phu-du text-[1.25rem] font-bold leading-[1.5rem] tracking-[0.025rem] text-white'>
                {translateContactInfo('directionsToTheOffice')}
              </span>
              <ICDirection className='xsm:w-[0.875rem] h-auto w-[1.5rem]' />
            </div>
          </Link>
        </div>
      )}
    </div>
  )
}

